SourceCode.Declaration:
/// <summary>
/// This attribute is used to find the class assigned to the form.
/// </summary>
/// <remarks>
/// This attribute should be used in the classes that implement
/// the <c>AssetIPostingLayerExtended</c> interface.
/// </remarks>
class AssetPostingLayerExtendedExtensionAttribute extends SysAttribute
{
    FormName formName;

}

Method.Source:
public FormName parmFormName(formName _formName = formName)
{
    formName = _formName;

    return formName;
}
