/// <summary>
///    The method indicates if the asset in the Acquired_CZ state can be disposed.
/// </summary>
/// <returns>
///    Always returns true, since the asset can be disposed in this state.
/// </returns>
public boolean canBeDisposed()
{
    return true;
}

/// <summary>
///    The method indicates if transactions can be posted to the asset in the Acquired_CZ state.
/// </summary>
/// <returns>
///    Always returns true, since the transactions can be posted to the asset.
/// </returns>
public boolean canBePostedTo()
{
    return true;
}
