/// <summary>
/// Indicates whether the contract is valid.
/// </summary>
/// <returns>
/// true if the contract is valid; otherwise, false.
/// </returns>
public boolean validate()
{
    boolean isValid = true;
    int months;

    if (includeBudgetModel && !budgetModelId)
    {
        isValid = checkFailed("@SYS71103");
    }

    if (showFiscalYear)
    {
        if (!fromDate && !todate)
        {
            isValid = checkFailed("@SYS39559");
        }
    }
    else if (!fromDate)
    {
        isValid = checkFailed("@SYS39559");
    }
    else if (!toDate)
    {
        isValid = checkFailed("@SYS39563");
    }

    if (fromDate && toDate)
    {
        if (fromDate > toDate)
        {
            isValid = checkFailed("@GLS222838");
        }

        if (!FiscalCalendars::areDatesInSameFiscalYear(CompanyInfoHelper::fiscalCalendarRecId(), fromDate, toDate))
        {
            isValid = checkFailed("@GLS222837");
        }
    }

    months = AssetTableInterval_Month::findIntervalForLongFiscalYear(fromDate, toDate, showFiscalYear);
    if (months != 0)
    {
        if (Box::okCancel(strFmt("@GLS222880", months), DialogButton::Cancel) == DialogButton::Cancel)
        {
            isValid= checkFailed("@GLS104964");
        }
    }

    return isValid;
}
