SourceCode.Declaration:
using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>AutoChargesSetupSequenceCompoundFeature</c> class defines the feature management feature, 
/// when enabled the usage of sequence and compound configuration can be setup for auto charges.
/// Provides the capability of define the order the charges are calculated and whether charge should be accumulated into the base cost calculation.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public final class AutoChargesSetupSequenceCompoundFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static AutoChargesSetupSequenceCompoundFeature singleton;

}

Method.Source:
[Hookable(false)]
public static AutoChargesSetupSequenceCompoundFeature instance()
{
    if (!singleton)
    {
        singleton = new AutoChargesSetupSequenceCompoundFeature();
    }

    return singleton;
}

[Hookable(false)]
public boolean canDisable()
{
    MarkupAutoLine markupAutoLine;
    select firstOnly crosscompany RecId from markupAutoLine
        where markupAutoLine.IsCompound == NoYes::Yes;

    if (markupAutoLine.RecId)
    {
        warning("@SCM:AutoChargesSetupSequenceCompoundFeatureDisableWarningAutoCharge");
        return false;
    }

    MarkupTrans markupTrans;
    select firstOnly crosscompany RecId from markupTrans
        where markupTrans.IsCompound == NoYes::Yes;

    if (markupTrans.RecId)
    {
        warning("@SCM:AutoChargesSetupSequenceCompoundFeatureDisableWarningChargeTransaction");
        return false;
    }

    return true;
}

internal boolean isFeatureAndConfigurationkeyEnabled()
{
    return this.isEnabled() && this.isConfigurationKeyEnabled();
}
