/// <summary>
/// Initializes invent transfer values from invent transfer journal.
/// </summary>
/// <param name = "_inventTransferJour">The invent transfer journal cursor.</param>
/// <returns>Invent transfer table instance.</returns>
public static AxInventTransferTable createFromInventTransferJour(InventTransferJour _inventTransferJour)
{
    AxInventTransferTable axInventTransferTable = new AxInventTransferTable();
    axInventTransferTable.parmInventLocationIdFrom(_inventTransferJour.InventLocationIdTo);
    axInventTransferTable.parmFromAddressName(_inventTransferJour.ToAddressName);
    axInventTransferTable.parmFromPostalAddress(_inventTransferJour.ToPostalAddress);

    axInventTransferTable.parmInventLocationIdTo(_inventTransferJour.InventLocationIdFrom);
    axInventTransferTable.parmToAddressName(_inventTransferJour.FromAddressName);
    axInventTransferTable.parmToPostalAddress(_inventTransferJour.FromPostalAddress);

    axInventTransferTable.parmInventLocationIdTransit(_inventTransferJour.InventLocationIdTransit);

    return axInventTransferTable;
}

