/// <summary>
///    Retrieves the record from the <c>Address</c> table.
/// </summary>
/// <param name="_axLogisticsLocation">
///    The <c>AxLogisticsLocation</c> to set; optional.
/// </param>
/// <returns>
///    The <c>AxLogisticsLocation</c> object.
/// </returns>
public AxLogisticsLocation inventLocationIdTo_AxLogisticsLocation(AxLogisticsLocation  _axLogisticsLocation = null)
{
    AxLogisticsLocation  axLogisticsLocation;
    InventLocation  inventLocation;

    if (! inventLocationIdTo_AxLogisticsLocationIdx)
    {
        inventLocationIdTo_AxLogisticsLocationIdx = this.nextCacheIdx();
    }

    if (! prmisDefault(_axLogisticsLocation))
    {
        this.classCacheInsert(inventLocationIdTo_AxLogisticsLocationIdx, axLogisticsLocation);
        axLogisticsLocation = _axLogisticsLocation;
    }
    else
    {
        if (this.classCacheExist(inventLocationIdTo_AxLogisticsLocationIdx))
        {
            axLogisticsLocation = this.classCacheLookup(inventLocationIdTo_AxLogisticsLocationIdx);
        }
        else
        {
            this.setInventLocationIdTo();
            inventLocation = InventLocation::find(this.parmInventLocationIdTo());
            axLogisticsLocation = AxLogisticsLocation::newLogisticsLocation(LogisticsLocationEntity::findLocation(inventLocation, LogisticsLocationRoleType::None).getLogisticsLocation());
            this.classCacheInsert(inventLocationIdTo_AxLogisticsLocationIdx, axLogisticsLocation);
        }
    }

    return axLogisticsLocation;
}

