/// <returns>
///    The <c>DlvModeId</c> value.
/// </returns>
public str parmDlvModeId(str _dlvModeId = '')
{
    DictField   dictField;
    str         dlvModeId;

    if (!prmisDefault(_dlvModeId))
    {
        dictField = new DictField(tableNum(InventTransferTable), fieldNum(InventTransferTable, DlvModeId));
        if (this.valueMappingInbound())
        {
            this.validateInboundString(_dlvModeId, dictField, this.mapPolicy().xMLMapDlvMode());
            dlvModeId = this.axDlvModeId(_dlvModeId);
        }
        else
        {
            this.validateInboundString(_dlvModeId, dictField);
        }
        if (dlvModeId)
        {
            this.setField(fieldNum(InventTransferTable, DlvModeId), dlvModeId);
        }
        else
        {
            this.setField(fieldNum(InventTransferTable, DlvModeId), _dlvModeId);
        }
    }
    if (this.valueMappingOutbound())
    {
        return this.axDlvModeId(inventTransferTable.DlvModeId);
    }
    else
    {
        return inventTransferTable.DlvModeId;
    }
}

