/// <summary>
///    Sets the value for the <c>DeliveryDateControlType</c> field.
/// </summary>
protected void setDeliveryDateControlType()
{
    SalesParameters     salesParameters;

    if (this.isMethodExecuted(funcName(), fieldNum(InventTransferTable, DeliveryDateControlType)))
    {
        return;
    }

    this.setTransferId();

    if (this.isFieldSet(fieldNum(InventTransferTable, TransferId)))
    {
        salesParameters = SalesParameters::find();

        this.parmDeliveryDateControlType(       salesParameters.DeliveryDateControlType);
        this.parmATPTimeFence(                  salesParameters.atpTimeFence);
        this.parmATPInclPlannedOrders(          salesParameters.atpInclPlannedOrders);
        this.parmATPBackwardDemandTimeFence(    salesParameters.atpBackwardDemandTimeFence);
        this.parmATPBackwardSupplyTimeFence(    salesParameters.atpBackwardSupplyTimeFence);
        this.parmATPApplyDemandTimeFence(       salesParameters.atpApplyDemandTimeFence);
        this.parmATPApplySupplyTimeFence(       salesParameters.atpApplySupplyTimeFence);
    }
}

