/// <summary>
///    Sets default fields for the table row.
/// </summary>
protected void setTableFields()
{
    // <GEEU>
    #ISOCountryRegionCodes
    // </GEEU>
    super();

    useMapPolicy = false;

    this.setTransferId();
    this.setInventLocationIdFrom();
    this.setInventLocationIdTo();
    this.setTransferStatus();
    this.setShipDate();
    this.setReceiveDate();
    this.setFromAddressName();
    this.setFromPostalAddress();
    this.setToAddressName();
    this.setToPostalAddress();
    this.setAutoReservation();
    this.setDlvModeId();
    this.setDlvTermId();
    this.setFreightZoneId();
    this.setFreightSlipType();
    this.setFromContactPerson();
    this.setToContactPerson();
    this.setInventLocationIdTransit();
    this.setDeliveryDateControlType();
    // <GEEU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT, #isoLV]))
    {
        this.setTrPackingSlipAutoNumbering_LT();
    }
    // </GEEU>
    this.setPdsOverrideFEFO();
    this.setRetailRetailStatusType();
    useMapPolicy = true;
}

