/// <summary>
///    Sets the value for the <c>TransferId</c> field.
/// </summary>
protected void setTransferId()
{
    NumberSequenceReference numberSequenceReference;

    if (this.isMethodExecuted(funcName(), fieldNum(InventTransferTable, TransferId)))
    {
        return;
    }

    if (this.isSetMethodsCalledFromSave())
    {
        if (this.isFieldSetExternally(fieldNum(InventTransferTable, TransferId)))
        {
            if (!this.inventTransferTable())
            {
                numberSequenceReference = InventParameters::numRefTransferId();
                this.checkNumber(numberSequenceReference.numberSequenceTable(),fieldNum(InventTransferTable,TransferId),this.parmTransferId());

                if (numberSequenceReference.NumberSequenceId && numberSequenceReference.numberSequenceTable().Continuous)
                {
                    NumberSeq::newReserveNum(numberSequenceReference).reserve(this.parmTransferId());
                }
            }
        }
        else
        {
            if (this.isFieldSet(fieldNum(InventTransferTable, TransferId)))
            {
                return;
            }

            if (! this.parmTransferId())
            {
                this.parmTransferId(NumberSeq::newGetNum(InventParameters::numRefTransferId()).num());
            }
        }
    }
}

