/// <summary>
///    Validates the record from the <c>InventTransfertable</c> table before it is saved.
/// </summary>
protected void validateWrite()
{
    SalesCalcAvailableDlvDates salesCalcAvailableDlvDates;

    super();

    if (this.parmDeliveryDateControlType())
    {
        salesCalcAvailableDlvDates = SalesCalcAvailableDlvDates::newCommonSalesDlvDateType(this.currentRecord(),fieldNum(InventTransferTable, ShipDate));
        salesCalcAvailableDlvDates.modifiedField(this.currentRecord(), false, true);
    }
}

