/// <summary>
///    Sets the <c>AifEndpointActionValueMap</c> table record from which the class should return values.
/// </summary>
/// <param name="_aifEndpointActionValueMap">
///    The <c>AifEndpointActionValueMap</c> record from which this class should return values.
/// </param>
private void setAifEndpointActionValueMap(AifEndpointActionValueMap _aifEndpointActionValueMap)
{
;
    aifEndpointActionValueMap.data(_aifEndpointActionValueMap);
    if (_aifEndpointActionValueMap)
    {
        recordExist = true;
    }
}

