static AxMapPolicy construct()
{
    return new AxMapPolicy();
}

static AxMapPolicy newAifEndpointActionValueMap(AifEndpointActionValueMap _aifEndpointActionValueMap)
{
    AxMapPolicy     axMapPolicy = AxMapPolicy::construct();
;
    axMapPolicy.setAifEndpointActionValueMap(_aifEndpointActionValueMap);

    return axMapPolicy;
}
