public str parmInvoiceAccount(str _invoiceAccount = '')
{
    DictField dictField;

    if (!prmisdefault(_invoiceAccount))
    {
        dictField = new DictField(tablenum(VendInvoiceInfoTable),fieldnum(VendInvoiceInfoTable,InvoiceAccount));
        if (this.valueMappingInbound())
        {
            this.validateInboundString(_invoiceAccount,dictField,this.mapPolicy().xMLMapVendAccount());
            _invoiceAccount = this.axVendAccount(_invoiceAccount);
        }
        else
        {
            this.validateInboundString(_invoiceAccount,dictField);
        }
        this.setField(fieldnum(VendInvoiceInfoTable, InvoiceAccount), _invoiceAccount);
    }

    if (this.valueMappingOutbound())
    {
        return this.axVendAccount(vendInvoiceInfoTable.InvoiceAccount);
    }
    else
    {
        return vendInvoiceInfoTable.InvoiceAccount;
    }
