}

Method.Source:
private static void typeNew()
{
    instance = new BOMCalcJobExcludeProdOrderFromCostCalculationFeature();
}

[Hookable(false)]
public static BOMCalcJobExcludeProdOrderFromCostCalculationFeature instance()
{
    if(!instance)
    {
        instance = new BOMCalcJobExcludeProdOrderFromCostCalculationFeature();
    }

    return instance;
}

[Hookable(false)]
public void onEnabled()
{
    ttsbegin;

    DataArea dataArea;

    while select id from dataArea where dataArea.isVirtual == NoYes::No
    {
        changecompany(dataArea.id)
        {
            BOMLevelCalc::setBOMLevelRecalculationNeeded();
        }
    }

    ttscommit;
}

[Hookable(false)]
public void onDisabled()
{
    // nothing to do
}
