class BOMconsistencyCheck extends SysConsistencyCheck implements BatchRetryable
{
}

Method.Source:
static void main(Args args)
{
    SysConsistencyCheckJob      consistencyCheckJob;

    consistencyCheckJob =  new SysConsistencyCheckJob(classnum(BOMconsistencyCheck));

    if (consistencyCheckJob.prompt())
        consistencyCheckJob.runOperation();
}

/// <summary>
/// Specifies if the batch task is retryable for transient exceptions or not.
/// </summary>
/// <returns>
/// If true is returned, the batch task is retryable, otherwise it is not.
/// </returns>
[Hookable(false)]
final boolean isRetryable() 
{
    return true;
}
