/// <summary>
/// Creates new bank document state attribute.
/// </summary>
/// <param name="_bankDocumentBookType">
/// The bank document book type.
/// </param>
/// <param name="_bankDocumentStatus">
/// The bank document status.
/// </param>
public void new(BankDocumentBookType _bankDocumentBookType, BankDocumentStatus _bankDocumentStatus)
{
    super();

    this.parmBankDocumentBookType(_bankDocumentBookType);
    this.parmBankDocumentStatus(_bankDocumentStatus);
}

    public bankDocumentBookType parmBankDocumentBookType(bankDocumentBookType _bankDocumentBookType = bankDocumentBookType)
{
    bankDocumentBookType = _bankDocumentBookType;
    return bankDocumentBookType;
}

    public BankDocumentStatus parmBankDocumentStatus(BankDocumentStatus _bankDocumentStatus = bankDocumentStatus)
{
    bankDocumentStatus = _bankDocumentStatus;
    return bankDocumentStatus;
}
