/// <summary>
///    The <c>BudgetSourceResolvedReference</c> class is used to capture the information
///    from a resolved budget source reference.
/// </summary>
public class BudgetSourceResolvedReference
{
    private RecId recId;

}

Method.Source:
/// <summary>
/// Gets or sets the <c>>RecId</c> value to be used.
/// </summary>
/// <param name = "_recId">The <c>RecId</c> value to set.</param>
/// <returns>The <c>RecId</c> value.</returns>
public RecId parmRecId(RecId _recId = recId)
{
    recId = _recId;
    return recId;
}

public static BudgetSourceResolvedReference construct()
{
    return new BudgetSourceResolvedReference();
}
