SourceCode.Declaration:
using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// A feature which allows to add business documents per legal entity in the electronic document parameters.
/// </summary>
[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class BusinessDocumentCompanySpecificDocumentTypesFeature implements IFeatureMetadata, IFeatureLifecycle, IFeatureMetadataEnablementNotifiable
{
    private static BusinessDocumentCompanySpecificDocumentTypesFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new BusinessDocumentCompanySpecificDocumentTypesFeature();
}

/// <summary>
/// Obtains the singleton object instance.
/// </summary>
/// <returns>A <c>BusinessDocumentCompanySpecificDocumentTypesFeature</c> instance.</returns>
[Hookable(false)]
public static BusinessDocumentCompanySpecificDocumentTypesFeature instance()
{
    return BusinessDocumentCompanySpecificDocumentTypesFeature::instance;
}

[Hookable(false)]
public void onDisabled()
{
    if (Box::yesNo("@BusinessDocumentSubmissionService:CompanySpecificDocumentTypesFeatureDisableConfirmation", DialogButton::No))
    {
        BusinessDocumentTypes businessDocumentTypes;

        delete_from businessDocumentTypes
            where businessDocumentTypes.LegalEntityId != '';
    }
    else
    {
        throw error("@SYS110943");
    }
}
