using System.ComponentModel.Composition;
using IFeatureLifecycle = Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureLifecycle;
using IFeatureMetadata = Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata;
using FeatureLifecycleStage = Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.FeatureLifecycleStage;

/// <summary>
/// Feature for executing update actions for submitted business documents.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class BusinessDocumentExecuteUpdateActionsFeature implements IFeatureLifecycle, IFeatureMetadata
{
    private static BusinessDocumentExecuteUpdateActionsFeature singleton;

}

Method.Source:
/// <summary>
/// Feature instance.
/// </summary>
/// <returns>Feature instance object.</returns>
[Hookable(false)]
public static BusinessDocumentExecuteUpdateActionsFeature instance()
{
    if (!singleton)
    {
        singleton = new BusinessDocumentExecuteUpdateActionsFeature();
    }

    return singleton;
}
