/// <summary>
/// Class describes attribute to construct successors of <c>CFMPaymentRequestState</c>.
/// </summary>
class CFMPaymentRequestStateAttribute extends SysAttribute
{
    CFMPaymentRequestStatus paymentRequestStatus;

}

Method.Source:
public CFMPaymentRequestStatus parmPaymentRequestStatus(CFMPaymentRequestStatus _paymentRequestStatus = paymentRequestStatus)
{
    paymentRequestStatus = _paymentRequestStatus;

    return paymentRequestStatus;
}
