using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public final class ChangeShippingLocationFeature implements IFeatureMetadata, IFeatureMetadataEnablementNotifiable, IFeatureLifecycle
{
    private static ChangeShippingLocationFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new ChangeShippingLocationFeature();
}

/// <summary>
/// Obtains the singleton object instance.
/// </summary>
/// <returns>The <c>WarrantyFeature</c> instance.</returns>
[Hookable(false)]
public static ChangeShippingLocationFeature instance()
{
    return ChangeShippingLocationFeature::instance;
}

[Hookable(false)]
public void onEnabled()
{
    RetailStoreLocatorGroupMember retailStoreLocatorGroupMember;

    ttsbegin;
    update_recordset retailStoreLocatorGroupMember
        setting     IsPickupLocation = true,
                    IsShippingLocation = true
        where       retailStoreLocatorGroupMember.Store != 0;
    ttscommit;
}

/// <summary>
/// Determines the feature is enabled.
/// </summary>
/// <returns>true if the feature is enabled; otherwise, false.</returns>
internal static boolean isEnabled()
{
    return Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.FeatureStateProvider::isFeatureEnabled(ChangeShippingLocationFeature::instance());
}
