/// <summary>
/// Simple args object used to transfer <c>boolean</c> value from call backs.
/// </summary>
class ChargesArgs
{
    boolean returnValue;

}

Method.Source:
/// <summary>
/// Gets the <c>boolean</c> value stored in object.
/// </summary>
/// <returns>
/// <c>boolean</c> value stored in obejct.
/// </returns>
public boolean getReturnValue()
{
    return returnValue;
}

/// <summary>
/// Sets the <c>boolean</c> value stored in object.
/// </summary>
/// <param name="_returnValue">
/// <c>boolean</c> value to store.
/// </param>
public void setReturnValue(boolean _returnValue)
{
    returnValue = _returnValue;
}
