public SysInstrumentationActivityContext populateIndirect(CreatedTransactionId _transactionId)
{
    SysInstrumentationActivity activity = logger.activity('populateIndirect');
    SysInstrumentationActivityContext activityContext = logger.activityContext(activity);
    activityContext.addCustomProperty(ActivityNamePrefix + TransactionId, int642Str(_transactionId));
    return activityContext;
}

