/// <summary>
/// The flight <c>CustFreeTextInvoiceTemplateFlight</c> when enabled and when the accounting distribution
/// template is modified on free text invoice template, the corresponding accounting distribution template on 
/// the line template is updated too.
/// </summary>
internal final class CustFreeTextInvoiceTemplateFlight extends Flight
{
    static private CustFreeTextInvoiceTemplateFlight singleton = new CustFreeTextInvoiceTemplateFlight();
}

Method.Source:
public static CustFreeTextInvoiceTemplateFlight instance()
{
    return singleton;
}
