using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>CustInvoiceTableCalcTotalsFeature</c> class enables or disables the ability to cache 
/// the totals calculated for a Free text invoice during posting.
/// The feature's enabled state for all or specific environments can be overridden 
/// using a <c>_KillSwitch</c> via ECS.
/// </summary>
[Export(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class CustInvoiceTableCalcTotalsFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static CustInvoiceTableCalcTotalsFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new CustInvoiceTableCalcTotalsFeature();
}

/// <summary>
/// Obtains the singleton object instance.
/// </summary>
/// <returns>The <c>CustInvoiceTableCalcTotalsFeature</c> instance.
/// </returns>
[Hookable(false)]
public static CustInvoiceTableCalcTotalsFeature instance()
{
    return instance;
}

/// <summary>
/// Determines whether the cache free text total amounts to the database feature is enabled.
/// </summary>
/// <returns>
/// Returns true if the feature in Feature Management is enabled; otherwise, false.
/// </returns>
public static boolean isEnabled()
{
    return Dynamics.AX.Application.FeatureStateProvider::isFeatureEnabled(CustInvoiceTableCalcTotalsFeature::instance());
}
