SourceCode.Declaration:
using System.ComponentModel.Composition;

/// <summary>
/// Implements the <c>ProcessScheduleISeriesRegistration</c> to register the settlement of prepayment invoice 'Series' with the Process Automation.
/// </summary>
[Export(identifierStr(Dynamics.AX.Application.ProcessScheduleISeriesRegistration))]
[ExportMetadata(classStr(ProcessScheduleISeriesRegistration), classStr(CustPrepaymentAutoSettlementServiceSeriesRegistration))]
internal final class CustPrepaymentAutoSettlementServiceSeriesRegistration implements ProcessScheduleISeriesRegistration
{
}

Method.Source:
[Hookable(false)]
public ProcessScheduleSeriesRegistrationItem getProcessScheduleSeriesRegistrationItem()
{
    ProcessScheduleSeriesRegistrationItem processScheduleSeriesRegistrationItem = ProcessScheduleSeriesRegistrationItem::construct();
    processScheduleSeriesRegistrationItem.parmDescription("@AccountsReceivable:PrePaymentAutoSettlementService");
    processScheduleSeriesRegistrationItem.parmOwnerId(curUserId());
    processScheduleSeriesRegistrationItem.parmProcessScheduleSeriesPatternList(this.getSeriesPatternList());
    processScheduleSeriesRegistrationItem.parmSeriesName("@AccountsReceivable:PrePaymentAutoSettlementService");
    processScheduleSeriesRegistrationItem.parmTypeName(CustPrepaymentAutoSettlementTypeRegistration::RegisteredTypeName);
    processScheduleSeriesRegistrationItem.parmProcessScheduleSeriesPatternList(this.getSeriesPatternList());

    return processScheduleSeriesRegistrationItem;
}

private List getSeriesPatternList()
{
    ProcessScheduleSeriesPatternItem processScheduleSeriesPatternItem = ProcessScheduleSeriesPatternItem::construct();
    processScheduleSeriesPatternItem.parmUnit(ProcessScheduleUnit::Hour);
    processScheduleSeriesPatternItem.parmPollingInterval(1);

    List list = new List(Types::Class);
    list.addEnd(processScheduleSeriesPatternItem);

    return list;
}
