using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.BusinessPlatform.SharedTypes;

/// <summary>
/// The <c>CustVendPDCSettleDateFeature</c> class allows the user to pick a transaction date for the newly generated
/// vouchers during settling the PDC if the current date time passed its maturity date.
/// </summary>
[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class CustVendPDCSettleDateFeature implements IFeatureMetadata
{
    private static CustVendPDCSettleDateFeature instance = new CustVendPDCSettleDateFeature();

}

Method.Source:
[Hookable(false)]
public static CustVendPDCSettleDateFeature instance()
{
    return CustVendPDCSettleDateFeature::instance;
}
