    public void formatTxt(int _pos, int _fromPos, int _toPos, str _name, str _record, str _descr = '')
{
    str value = substr(_record,_fromPos,_toPos-_fromPos+1);
    str record;

    record =
    num2Str0(_pos,2)+': '+
    num2Str0(_fromPos,2)+'-'+
    num2Str0(_toPos,2)+', '+
    num2Str0(_toPos-_fromPos+1,2)+' '+
    strLFix(_name,30)+' '+
    value +
    (_descr ? ' '+ _descr : '');

    this.write(record);
}

    public ClassName interfaceName()
{
    return 'BBS Ocr, AG, eFaktura, DirRem (NO)';
}

    public void processRecord(container _record)
{
    container   recordCon;
    str         record;
    str         recordType;
    str         tjenesteKode;
    str         type;
    ;

    recordCon   = _record;
    if (recordCon)
    {
        record      = conpeek(recordCon,1);
        tjenesteKode= substr(record,3,2);
        type        = substr(record,5,2);
        recordType  = substr(record,7,2);

        this.record(recordCon);      //write the record line
        switch (recordType)
        {
            case '10':   //Forsendelse Start
                this.record000010(record);
                break;
            case '89':   //Forsendelse Slutt
                this.record000089(record);
                break;
        }
        switch (tjenesteKode)
        {
            case '04':   //BBS Direkte Remittering
            {
                switch (recordType)
                {
                    case '10':   //Forsendelse Start
                        //Se første Switch
                        break;
                    case '20':   //Oppdrag Start
                        this.record04xx20(record);
                        break;
                    case '30':   //DirRem Beløpspost 1
                        this.record04xx30(record);
                        break;
                    case '31':   //DirRem Beløpspost 2
                        this.record04xx31(record);
                        break;
                    case '40':   //DirRem Navn record
                        this.record04xx40(record);
                        break;
                    case '41':   //DirRem Adresserecord
                         this.record04xx41(record);
                         break;
                    case '49':   //DirRem Spesifikasjonsrecord
                        this.record04xx49(record);
                        break;
                    case '50':   //DirRem Underspesifikasjon
                        this.record04xx50(record);
                        break;
                    case '88':   //Oppdrag Slutt
                        this.record04xx88(record);
                        break;
                    case '89':   //Forsendelse Slutt
                        //Se første Switch
                        break;
                    default:
                        info(strfmt('Ukjent RecordType %1',recordType));
                        this.recordUnknown(record);
                        break;
                }
                break;
            }
            case '09':   //BBS OCr
            {
                switch (recordType)
                {
                    case '10':   //Forsendelse Start
                        //Se første Switch
                        break;
                    case '20':   //OCR Oppdrag Start
                        this.record09xx20(record);
                        break;
                    case '30':   //OCR Beløpspost 1
                        this.record09xx30(record);
                        break;
                    case '31':   //OCR Beløpspost 2
                        this.record09xx31(record);
                        break;
                    case '32':   //OCR Beløpspost 3
                        this.record09xx32(record);
                        break;
                    case '88':   //OCR Oppdrag Slutt
                        this.record09xx88(record);
                        break;
                    case '89':   //Forsendelse Slutt
                         //Se første Switch
                         break;
                    default:
                         info(strfmt('Ukjent RecordType %1',recordType));
                         this.recordUnknown(record);
                         break;
                }
                break;
            }
            case '21':   //BBS AvtaleGiro
            {
                switch (recordType)
                {
                    case '10':   //Forsendelse Start
                        //Se første Switch
                        break;
                    case '20':   //AvtaleGiro Oppdrag Start
                    {
                        switch (type)
                        {
                            case '24':  //AvtaleGiro faste betalingsoppdrag
                                this.record212420(record);
                                break;
                            default:
                                this.record21xx20(record);
                                break;
                        }
                        break;
                    }
                    case '30':   //AvtaleGiro Beløpspost 1
                       this.record21xx30(record);
                       break;
                    case '31':   //AvtaleGiro Beløpspost 2
                        this.record21xx31(record);
                        break;
                    case '49':   //AvtaleGiro Spesifikasjonsrecord
                        this.record21xx49(record);
                        break;
                    case '70':   //AvtaleGiro Spesifikasjonsrecord
                    {
                        switch (type)
                        {
                            case '94':  //AvtaleGiro faste betalingsoppdrag
                                this.record219470(record);
                                break;
                            default:
                                info(strfmt('Ukjent RecordType %1',recordType));
                                this.recordUnknown(record);
                                break;
                        }
                        break;
                    }
                    case '88':   //AvtaleGiro Oppdrag Slutt
                    {
                        switch (type)
                        {
                            case '24':  //AvtaleGiro faste betalingsoppdrag
                                this.record212488(record);
                                break;
                            default:
                                this.record21xx88(record);
                                break;
                        }
                        break;
                    }
                    case '89':   //Forsendelse Slutt
                        //Se første Switch
                        break;
                    default:
                        info(strfmt('Ukjent RecordType %1',recordType));
                        this.recordUnknown(record);
                        break;
                }
                break;
            }
            case '42':   //eFaktura Fakturafil
            {
                switch (recordType)
                {
                    case '10':   //Forsendelse Start
                        //Se første Switch
                        break;
                    case '20':   //eFaktura Oppdrag Start
                        this.record42xx20_SectionStart(record);
                        break;
                    case '22':   //eFaktura Adressepost 3
                        this.record42xx22_Address3(record);
                        break;
                    case '23':   //eFaktura Adressepost 4
                        this.record42xx23_Address4(record);
                        break;
                    case '28':   //User-Id
                        this.record42xx28_UserId(record);
                        break;
                    case '30':   //eFaktura Beløpspost 1
                        this.record42xx30_Amount1(record);
                        break;
                    case '31':   //eFaktura Beløpspost 2
                        this.record42xx31_Amount2(record);
                        break;
                    case '34':   //eFaktura Referanse 1
                        this.record42xx34_Reference1(record);
                        break;
                    case '35':   //eFaktura Referanse 2
                        this.record42xx35_Reference2(record);
                        break;
                    case '36':   //eFaktura Referanse 3
                        this.record42xx36_Reference3(record);
                        break;
                    case '37':   //eFakutra Avtale 1
                        this.record42xx37_Agreement1(record);
                        break;
                    case '38':   //eFakutra Avtale 2
                        this.record42xx38_Agreement2(record);
                        break;
                    case '39':   //eFakutra Navn
                        this.record42xx39_Name(record);
                        break;
                    case '40':   //eFaktura Adressepost 1
                        this.record42xx40_Address1(record);
                        break;
                    case '41':   //eFaktura Adressepost 2
                        this.record42xx41_Address2(record);
                        break;
                    case '49':   //eFaktura Spesifikasjonsrecord
                        this.record42xx49_Spesification(record);
                        break;
                    case '63':   //eFaktura Oppdrag Start
                        this.record42xx63_SectionStart(record);
                        break;
                    case '64':   //eFaktura Oppdrag Start
                        this.record42xx64_SectionStart(record);
                        break;
                    case '65':   //eFaktura Feilkode
                        this.record42xx65_ErrorCode(record);
                        break;
                    case '67':   //eFaktura Oppdrag Slutt
                        this.record42xx67_SectionEnd(record);
                        break;
                    case '68':   //eFaktura Oppdrag Slutt
                        this.record42xx68_SectionEnd(record);
                        break;
                    case '88':   //eFaktura Oppdrag Slutt
                        this.record42xx88_SectionEnd(record);
                        break;
                    case '89':   //Forsendelse Slutt
                        //Se første Switch
                        break;
                    default:
                        info(strfmt('Ukjent RecordType %1',recordType));
                        this.recordUnknown(record);
                        break;
                }
                break;
            }
        }
    }
}

    void record(container _record)
{
    if (conpeek(_record,1))
    {
        this.write(' ');
        this.write(' ');
        this.write('Record');
        this.write(strfmt('Line: %1', conpeek(_record,1)));  //the line that is read
    }
}

