    public void record09xx30(str _record)
{
    str descr;
    str value;
    ;
    value = substr(_record,5,2);
    descr = value == '10' ? 'Transaksjon fra Giro belastet konto'   :
            value == '11' ? 'Transaksjon fra Faste Oppdrag'         :
            value == '12' ? 'Transaksjon fra Direkte Remittering'   :
            value == '13' ? 'Transaksjon fra BTG (Bedrifts Terminal Giro)' :
            value == '14' ? 'Transaksjon fra Skranke Giro'          :
            value == '15' ? 'Transaksjon fra AvtaleGiro'            :
            value == '16' ? 'Transaksjon fra TeleGiro'              :
            value == '17' ? 'Transaksjon fra Giro betalt kontant'   :
            value == '18' ? 'Transaksjon fra Bet.Term. Reversering m/KID'   :
            value == '19' ? 'Transaksjon fra Bet.Term. Kjøp m/KID'   :
            value == '20' ? 'Transaksjon fra Bet.Term. Reversering m/Fritekst (u/KID)' :
            value == '21' ? 'Transaksjon fra Bet.Term. Kjøp m/Fritekst (u/KID)'   :
            '';

    this.write('');
    this.write('OCR BELØPSPOST 1');
    this.formatTxt(1,1,2,'Formatkode',_record);
    this.formatTxt(2,3,4,'TjenesteKode',_record);
    this.formatTxt(3,5,6,'TransType',_record,descr);
    this.formatTxt(4,7,8,'RecordType',_record);
    this.formatTxt(5,9,15,'Transaskjons Nr',_record);
    this.formatTxt(6,16,21,'Oppgjørs Dato',_record);
    this.formatTxt(7,22,23,'Sentral Id',_record);
    this.formatTxt(8,24,25,'DagKode',_record);
    this.formatTxt(9,26,26,'Delavregnings nummer',_record);
    this.formatTxt(10,27,31,'Løpenummer',_record);
