    this.write('');
    this.write('eFaktura OPPDRAG START ' + descr);
    this.formatTxt(1,1,2,'Formatkode',_record);
    this.formatTxt(2,3,4,'TjenesteKode',_record);
    this.formatTxt(3,5,6,'OppdragsType',_record);
    this.formatTxt(4,7,8,'RecordType',_record);

    this.formatTxt(5,9,16,'Data avsender',_record);
    this.formatTxt(6,17,23,'Forsendelses Nr',_record);
    this.formatTxt(7,24,31,'Data mottaker',_record);
    this.formatTxt(8,32,35,'FILLER',_record);

    this.formatTxt(9,36,49,'Utsteders ref. i BBS',_record);
    this.formatTxt(10,50,50,'Status forsendelse',_record);

    value = substr(_record,51,3);
    this.formatTxt(11,51,53,'Kode',_record,this.codeTxt(value));

    this.formatTxt(12,54,80,'FILLER',_record);
}

