    public void record42xx88_SectionEnd(str _record)
{
    str value;
    ;
    value = substr(_record,5,2); //oppdragstype

    this.write('');
    this.write('eFaktura OPPDRAG SLUTT');  //Oppdrag slutt
    this.formatTxt(1,1,2,'Formatkode',_record);
    this.formatTxt(2,3,4,'TjenesteKode',_record);
    this.formatTxt(3,5,6,'OppdragsType',_record);
    this.formatTxt(4,7,8,'RecordType',_record);

    this.formatTxt(5,9,16,'Antall fakturaer',_record);
    this.formatTxt(6,17,24,'Antall records',_record);
    if (value == '94')
    {
        this.formatTxt(7,25,80,'FILLER',_record);
    }
    else
    {
        this.formatTxt(7,25,41,'Sum Beløp',_record);
        this.formatTxt(8,42,47,'Første forfalls Dato',_record);
        this.formatTxt(9,48,53,'Siste forfalls Dato',_record);
        this.formatTxt(10,54,67,'Avsenders ref i BBS',_record);
        this.formatTxt(11,68,80,'FILLER',_record);
    }
}

