/// <summary>
/// The <c>DirPersonInfoData</c> class acts as a data contract for the <c>DirPersonInfoService</c>,
/// and it is the type of object returned when person information is requested.
/// </summary>
[DataContractAttribute]
public class DirPersonInfoData
{
    LogisticsElectronicAddressLocator   personCommunicatorSignIn;
    LogisticsElectronicAddressLocator   personPrimaryEmail;
    DirPartyName                        personName;
    HcmTitleId                          workerTitle;
    UserId                              personUserId;
}

