/// <summary>
/// Gets or sets the person communicator sign-in parameter.
/// </summary>
/// <param name="_personCommunicatorSignIn">
/// The value to set.
/// </param>
/// <returns>
/// The value of the personCommunicatorSignIn parameter.
/// </returns>
[DataMemberAttribute]
public LogisticsElectronicAddressLocator parmPersonCommunicatorSignIn(LogisticsElectronicAddressLocator _personCommunicatorSignIn = personCommunicatorSignIn)
{
    personCommunicatorSignIn = _personCommunicatorSignIn;
    return personCommunicatorSignIn;
}

