/// <summary>
/// Obtains the singleton object instance.
/// </summary>
/// <returns>
/// An <c>CategoryHierarchy_Feature</c> instance.
/// </returns>
[Hookable(false)]
public static DutchICPDeclarationByTaxId_Feature instance()
{
    return DutchICPDeclarationByTaxId_Feature::instance;
}

[Hookable(false)]
public boolean isEnabled()
{
    TaxIntegrationTaxServiceParameters taxServiceParameter = TaxIntegrationTaxServiceParameters::find();
    return taxServiceParameter.IsMultipleTaxIdEnabled
        && (taxServiceParameter.IsEnable
            || (Dynamics.AX.Application.FeatureStateProvider::isFeatureEnabled(TaxServiceUniversalTaxRateAPIFeature::instance())
                && taxServiceParameter.IsISVTaxSolutionProviderEnabled));
}

