/// <summary>
/// This class holds the shipment stat information related to a EFDocument_BR and is used to
/// provide data to be written at the NF-e XML.
/// </summary>
public class EFiscalDocumentShipmentStat_BR
{
    GrossWeight          grossWeight;
    ItemNetWeight        netWeight;
    VolumeType_BR        volumeType;
    VolumeQty_BR         volumeQuantity;
    DlvTransportBrand_BR packingBrand;
}

