using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.BusinessPlatform.SharedTypes;

/// <summary>
/// Represents an activation export channels for Electronic Invoicing Service.
/// </summary>
[Export(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[InternalUseOnly]
internal final class EInvoicingExportChannelFeature implements Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata, Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureLifecycle
{
    private static EInvoicingExportChannelFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new EInvoicingExportChannelFeature();
}

/// <summary>
/// Obtains the singleton object instance.
/// </summary>
/// <returns>An <c>EInvoicingExportChannelFeature</c> instance.</returns>
[Hookable(false)]
public static EInvoicingExportChannelFeature instance()
{
    return EInvoicingExportChannelFeature::instance;
}

/// <summary>
/// Determines whether the feature is enabled.
/// </summary>
/// <returns>True, if the feature is enabled; otherwise, false.</returns>
[Hookable(false)]
public static boolean isEnabled()
{
    return ElectronicInvoicingServiceIntegrationFeature::isEnabled()
        && Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.FeatureStateProvider::isFeatureEnabled(
            EInvoicingExportChannelFeature::instance());
}
