[Hookable(false)]
public void onDisabled()
{
    if (Box::yesNo(
        strFmt(
            "@TaxMultipleVATID:OnDisableFeatureNotification",
            "@EUSalesListCommon:EUSalesListReportingFeature",
            "@EUSalesListCommon:EUSalesList"),
        DialogButton::No) == DialogButton::No)
    {
        throw error("@SYS110943");
    }

    ttsbegin;

    DataArea dataArea;
    while select id from dataArea where dataArea.isVirtual == NoYes::No
    {
        changecompany(dataArea.id)
        {
            TaxIntegrationTaxServiceParameters parameters = TaxIntegrationTaxServiceParameters::find(true);
            if (parameters.IsMultipleTaxIdEUSalesListEnabled)
            {
                parameters.IsMultipleTaxIdEUSalesListEnabled = NoYes::No;
                parameters.doUpdate();
            }
        }
    }

    ttscommit;
}
