using System.ComponentModel.Composition;

[ExportMetadata(identifierStr(TableName), tableStr(RetailInventTable))]
[ExportMetadata(identifierStr(EntityName), dataentityviewstr(EcoResReleasedProductDVEntity))]
[Export(identifierstr(Dynamics.AX.Application.IBusinessEventsTableToEntityRecordIdSelector))]
internal final class EcoResReleasedProductDVEntityRecordIdSelectorRetailInventTable implements IBusinessEventsTableToEntityRecordIdSelector
{
}

Method.Source:
[Hookable(false)]
public List getEntityRecordIdsImpactedByTableChange(BusinessEventsDefinition _eventDefinition, BusinessEventsActions _changeType, RecId _changedRecordId)
{
    if (_eventDefinition.DataSourceName != dataEntityDataSourceStr(EcoResReleasedProductDVEntity, RetailInventTable)
        || !EcoResReleasedProductDVEntitiesUseRecordIdSelectorsFlight::instance().isEnabled())
    {
        return null;
    }

    List impactedRecIds = new List(Types::Int64);

    InventTable inventTable;
    RetailInventTable retailInventTable;

    while select RecId from inventTable
        exists join retailInventTable
        where inventTable.ItemId == retailInventTable.ItemId
            && retailInventTable.RecId == _changedRecordId
    {
        impactedRecIds.addEnd(inventTable.RecId);
    }

    return impactedRecIds;
}
