protected void add(BudgetMap budget)
{
    balances.amount += CurrencyExchangeHelper::mstAmount(budget.amount, budget.currency, budget.startDate);
    balances.qty += budget.inventQty;
    balances.PdsCWQty +=  budget.PdsCWQty;
}

