/// <summary>
/// Retrieves an instance of the <c>FormLetterServiceControllerCheckPrintSettingsToggle</c> class.
/// </summary>
/// <returns>
/// The created instance.
/// </returns>
[Hookable(false)]
static internal FormLetterServiceControllerCheckPrintSettingsToggle instance()
{
    if (!singleton)
