/// <summary>
/// When <c>GanttControlSchedulerTimeZoneFlight</c> is enabled, the capacity reservations generated by the scheduler are used
/// and the user time zone is applied before Gantt chart scheduling.
/// </summary>
internal final class GanttControlSchedulerTimeZoneFlight extends Flight
{
    static private GanttControlSchedulerTimeZoneFlight singleton = new GanttControlSchedulerTimeZoneFlight();

}

Method.Source:
[Hookable(false)]
public static GanttControlSchedulerTimeZoneFlight instance()
{
    return singleton;
}
