/// <summary>
/// HcmWorker HcmEmploymentContractor_PurchID form control OnLookup event handler for the HcmWorker form.
/// </summary>
/// <param name="_sender">The form control raising the event.</param>
/// <param name="_e">Args for the event.</param>
[FormControlEventHandler(formControlStr(HcmWorker, HcmEmploymentContractor_PurchID), FormControlEventType::Lookup)]
public static void HcmEmploymentContractor_PurchID_OnLookup(FormControl _sender, FormControlEventArgs _e)
{
    CompanyInfo companyInfo;
    QueryBuildDataSource dsPurchID;
    Query query = new Query();
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(PurchTable), _sender);

    sysTableLookup.addLookupfield(fieldNum(PurchTable, PurchId));

    FormRun hcmWorkerForm = _sender.formRun();
    FormDataSource hcmEmployment_ds = hcmWorkerForm.dataSource(formDataSourceStr(HcmWorker, HcmEmployment)) as FormDataSource;
    HcmEmployment employment = hcmEmployment_ds.cursor();
    select firstonly DataArea, RecId from companyInfo where companyInfo.RecId == employment.LegalEntity;

    changecompany(companyInfo.DataArea)
    {
        // Instantiate sysTableLookup object using table which will provide the visible fields
        dsPurchID = query.addDataSource(tableNum(PurchTable));

        // Perform the lookup
        sysTableLookup.parmQuery(query);
        sysTableLookup.performFormLookup();
    }

    FormControlCancelableSuperEventArgs ce = _e as FormControlCancelableSuperEventArgs;
    ce.CancelSuperCall();
}

/// <summary>
/// Updates the worker form controls based on the employment type of the worker.
/// </summary>
/// <param name = "_formElement">The worker form instance that raised the event.</param>
/// <param name = "_hasEmploymentInCurrentCompany">Indicates whether or not the worker is employed by the current company.</param>
/// <param name = "_isEmployeeEmployment">Indicates whether or not the worker is employed as an employee.</param>
[SubscribesTo(FormStr(HcmWorker), delegatestr(HcmWorker, employmentControlsUpdating))]
public static void employmentControlsUpdatingHandler(FormRun _formElement, boolean _hasEmploymentInCurrentCompany, boolean _isEmployeeEmployment)
{
    FormActionPaneTabControl compensationPaneTabControl = _formElement.design().controlName(formControlStr(HcmWorker, compensationPaneTab)) as FormActionPaneTabControl;

    // Restricting visibility of compensation tab for commerce essentials
    if (!RetailSMB::IsRetailSMBEnabled())
    {
        compensationPaneTabControl.visible(_isEmployeeEmployment);
    }
    else
    {
        FormTabPageControl absencePageControl = _formElement.design().controlName(formControlStr(HcmWorker, absencePage)) as FormTabPageControl;
        FormTabPageControl financialDimensionsTabPageControl = _formElement.design().controlName(formControlStr(HcmWorker, financialDimensionsTabPage)) as FormTabPageControl;
        FormActionPaneTabControl projectPaneTabControl = _formElement.design().controlName(formControlStr(HcmWorker, ProjectPaneTab)) as FormActionPaneTabControl;

        compensationPaneTabControl.visible(false);
        absencePageControl.visible(false);
        financialDimensionsTabPageControl.visible(false);
        projectPaneTabControl.visible(false);
    }

    SecurityRights securityRights = SecurityRights::construct();
    boolean timeAccess = securityRights.formControlAccessRight(formStr(HcmWorker), formControlStr(HcmWorker, TimeRegistrationTabPage));
    FormDataSource jmgEmployee_ds = _formElement.dataSource(formDataSourceStr(HcmWorker, JmgEmployee)) as FormDataSource;
    FormDataSource employment_ds = _formElement.dataSource(formDataSourceStr(HcmWorker, HcmEmployment)) as FormDataSource;

    //
    // If the worker has no active employments then disable the related data source;
    // otherwise the worker has at least one active employment so enable the related data source.
    //
    FormDataSource hcmEmploymentContractor_ds = _formElement.dataSource(formDataSourceStr(HcmWorker, HcmEmploymentContractor)) as FormDataSource;
    hcmEmploymentContractor_ds.allowCreate(_hasEmploymentInCurrentCompany);
    hcmEmploymentContractor_ds.allowEdit(_hasEmploymentInCurrentCompany);
    hcmEmploymentContractor_ds.allowDelete(_hasEmploymentInCurrentCompany);

    if (timeAccess)
    {
        jmgEmployee_ds.executeQuery();
    }

    boolean isBorrowedInCurrentCompanyContext = false;
    FormDataSource hcmWorker_ds = _formElement.dataSource(formDataSourceStr(HcmWorker, HcmWorker)) as FormDataSource;

    if (!_hasEmploymentInCurrentCompany)
    {
        isBorrowedInCurrentCompanyContext = HcmWorkerFormExtensionAppSuiteHandler::borrowedInCurrentCompany(hcmWorker_ds.cursor().RecId);
    }

    FormTabPageControl timeRegistrationTabPageControl = _formElement.design().controlName(formControlStr(HcmWorker, TimeRegistrationTabPage)) as FormTabPageControl;
    FormActionPaneTabControl projectPaneTabControl = _formElement.design().controlName(formControlStr(HcmWorker, ProjectPaneTab)) as FormActionPaneTabControl;
    FormFunctionButtonControl actionPaneTermsofEmploymentControl = _formElement.design().controlName(formControlStr(HcmWorker, ActionPane_TermsofEmployment)) as FormFunctionButtonControl;
    FormFunctionButtonControl actionPaneAbsenceHistoryControl = _formElement.design().controlName(formControlStr(HcmWorker, ActionPane_AbsenceHistory)) as FormFunctionButtonControl;
    FormFunctionButtonControl actionPaneAbsenceTransactionsControl = _formElement.design().controlName(formControlStr(HcmWorker, ActionPane_AbsenceTransactions)) as FormFunctionButtonControl;
    FormDropDialogButtonControl generalSetup_PurchRFQControl = _formElement.design().controlName(formControlStr(HcmWorker, GeneralSetup_PurchRFQ)) as FormDropDialogButtonControl;
    FormFunctionButtonControl generalSetup_SMADispatchControl = _formElement.design().controlName(formControlStr(HcmWorker, GeneralSetup_SMADispatch)) as FormFunctionButtonControl;
    timeRegistrationTabPageControl.visible(timeAccess && _hasEmploymentInCurrentCompany);
    boolean projectOperationsIntegrationEnabled = ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();
    projectPaneTabControl.enabled(_hasEmploymentInCurrentCompany && !projectOperationsIntegrationEnabled);
    projectPaneTabControl.visible(_hasEmploymentInCurrentCompany && !projectOperationsIntegrationEnabled);
    actionPaneTermsofEmploymentControl.enabled(_hasEmploymentInCurrentCompany || isBorrowedInCurrentCompanyContext);
    actionPaneAbsenceHistoryControl.enabled(_hasEmploymentInCurrentCompany || isBorrowedInCurrentCompanyContext);
    actionPaneAbsenceTransactionsControl.enabled(_hasEmploymentInCurrentCompany || isBorrowedInCurrentCompanyContext);
    generalSetup_PurchRFQControl.visible(_hasEmploymentInCurrentCompany);
    generalSetup_SMADispatchControl.visible(_hasEmploymentInCurrentCompany);

    HcmWorkerFormExtensionAppSuiteHandler::updateTimeAndProjectAccessControls(_formElement);
}

private static void updateTimeAndProjectAccessControls(FormRun _formElement)
{
    SecurityRights securityRights = SecurityRights::construct();
    boolean timeAccess = securityRights.formControlAccessRight(formStr(HcmWorker), formControlStr(HcmWorker, TimeRegistrationTabPage));

    FormDataSource hcmWorker_ds = _formElement.dataSource(formDataSourceStr(HcmWorker, HcmWorker)) as FormDataSource;
    FormDataSource hcmEmployment_ds = _formElement.dataSource(formDataSourceStr(HcmWorker, HcmEmployment)) as FormDataSource;
    FormDataSource jmgEmployee_ds = _formElement.dataSource(formDataSourceStr(HcmWorker, JmgEmployee)) as FormDataSource;

    boolean employedInCurrentCompanyContext = false;
    boolean isEmployeeInCurrentCompany = false;
    boolean isContractorEmployment = false;
    HcmEmployment employment = hcmEmployment_ds.cursor();

    if (employment.RecId != 0)
    {
        employedInCurrentCompanyContext = true;

        if (employment.EmploymentType == HcmEmploymentType::Employee)
        {
            // The worker is an employee
            isEmployeeInCurrentCompany = true;
        }
        else
        {
            // The worker is a contractor
            isContractorEmployment = true;
        }
    }

    FormFunctionButtonControl generalSetup_ProjectControl = _formElement.design().controlName(formControlStr(HcmWorker, GeneralSetup_Project)) as FormFunctionButtonControl;
    FormFunctionButtonControl projProjectControl = _formElement.design().controlName(formControlStr(HcmWorker, ProjProject)) as FormFunctionButtonControl;
    FormFunctionButtonControl projCategoryControl = _formElement.design().controlName(formControlStr(HcmWorker, ProjCategory)) as FormFunctionButtonControl;
    FormButtonGroupControl projectControlControl = _formElement.design().controlName(formControlStr(HcmWorker, ProjectControl)) as FormButtonGroupControl;
    FormButtonGroupControl projTransactionControl = _formElement.design().controlName(formControlStr(HcmWorker, ProjTransaction)) as FormButtonGroupControl;
    FormButtonGroupControl projViewControl = _formElement.design().controlName(formControlStr(HcmWorker, ProjView)) as FormButtonGroupControl;
    FormDropDialogButtonControl generalSetup_PurchRFQControl = _formElement.design().controlName(formControlStr(HcmWorker, GeneralSetup_PurchRFQ)) as FormDropDialogButtonControl;
    FormFunctionButtonControl generalSetup_SMADispatchControl = _formElement.design().controlName(formControlStr(HcmWorker, GeneralSetup_SMADispatch)) as FormFunctionButtonControl;
    generalSetup_ProjectControl.enabled(employedInCurrentCompanyContext);
    projProjectControl.enabled(employedInCurrentCompanyContext);
    projCategoryControl.enabled(employedInCurrentCompanyContext);
    projectControlControl.enabled(employedInCurrentCompanyContext);
    projTransactionControl.enabled(employedInCurrentCompanyContext);
    projViewControl.enabled(employedInCurrentCompanyContext);
    generalSetup_PurchRFQControl.enabled(employedInCurrentCompanyContext);
    generalSetup_SMADispatchControl.enabled(employedInCurrentCompanyContext);

    // Set visibility to controls that should only be visible if the worker's employment is of type Contractor
    FormTabPageControl contractorDetailsTabPageControl = _formElement.design().controlName(formControlStr(HcmWorker, ContractorDetailsTabPage)) as FormTabPageControl;
    contractorDetailsTabPageControl.visible(isContractorEmployment);

    HcmWorker worker = hcmWorker_ds.cursor();
    boolean isWorkerStatusTerminated = worker.workerStatus() == HcmWorkerStatus::Terminated;

    FormTabPageControl detailsPanelControl = _formElement.design().controlName(formControlStr(HcmWorker, DetailsPanel)) as FormTabPageControl;
    FormButtonGroupControl timeRegistrationReportsGroupControl = _formElement.design().controlName(formControlStr(HcmWorker, TimeRegistrationReportsGroup)) as FormButtonGroupControl;
    FormButtonGroupControl timeRegistrationInquiryGroupControl = _formElement.design().controlName(formControlStr(HcmWorker, TimeRegistrationInquiryGroup)) as FormButtonGroupControl;
    FormFunctionButtonControl whsWorkerControl = _formElement.design().controlName(formControlStr(HcmWorker, WHSWorker)) as FormFunctionButtonControl;
    FormFunctionButtonControl mbTaxRegistrationControl = _formElement.design().controlName(formControlStr(HcmWorker, MbTaxRegistration)) as FormFunctionButtonControl;

    // Make visibility/enability adjustments depending on whether the Grid or Details is currently visible
    if (detailsPanelControl.isActivePage())
    {
        if (timeAccess)
        {
            timeRegistrationReportsGroupControl.visible(false);
            timeRegistrationInquiryGroupControl.visible(false);
        }

        whsWorkerControl.enabled(true);
        mbTaxRegistrationControl.enabled(true);
    }
    else
    {
        boolean  hasFlexProfile;
        boolean  isJmgEmployee;

        if (timeAccess)
        {
            JmgEmployee employee = jmgEmployee_ds.cursor();
            isJmgEmployee = (employee.Worker == worker.RecId);
            hasFlexProfile = employee.FlexProfile;

            FormButtonGroupControl maintainButtonGroupControl = _formElement.design().controlName(formControlStr(HcmWorker, maintainButtonGroup)) as FormButtonGroupControl;

            maintainButtonGroupControl.enabled(!isJmgEmployee && employedInCurrentCompanyContext);
        }

        SelectableDataArea selectedLegalEntityDataAreaId = _formElement.getSelectedLegalEntityDataAreaId();

        if (timeAccess)
        {
            timeRegistrationReportsGroupControl.visible(true);
            timeRegistrationReportsGroupControl.enabled(isJmgEmployee && isEmployeeInCurrentCompany);

            timeRegistrationInquiryGroupControl.visible(true);
            timeRegistrationInquiryGroupControl.enabled(isJmgEmployee && isEmployeeInCurrentCompany);

            FormFunctionButtonControl flexOverviewControl = _formElement.design().controlName(formControlStr(HcmWorker, FlexOverview)) as FormFunctionButtonControl;
            flexOverviewControl.enabled(isJmgEmployee && hasFlexProfile);
        }

        whsWorkerControl.enabled(false);
        mbTaxRegistrationControl.enabled(false);
    }
}

/// <summary>
/// Determine if the worker who is hired by another LE is a valid borrowed worker
/// </summary>
/// <param name = "_hcmWorkerRecId">RecID of worker.</param>
/// <returns>
/// Return true if the worker is a valid borrowed worker
/// </returns>
public static boolean borrowedInCurrentCompany(RecId _hcmWorkerRecId)
{
    HcmEmployment           employmentTable;
    CompanyInfo             companyTable;
    LedgerInterCompany      ledgerInterCompany;
    WorkCalendarEmployment  employmentCalendar;
    ProjTransferPrice       projectTransferPrice;
    ProjParameters          projParameters;

    select firstonly crossCompany RecId, LegalEntity from employmentTable
        where employmentTable.Worker == _hcmWorkerRecId
            && employmentTable.LegalEntity != CompanyInfo::current()
            && !(employmentTable.ValidTo < DateTimeUtil::getSystemDateTime())
            && !(employmentTable.ValidFrom > DateTimeUtil::maxValue())
        join DataArea from companyTable
            where companyTable.RecId == employmentTable.LegalEntity
        join RecId from ledgerInterCompany
            where ledgerIntercompany.OriginatingCompany == companyTable.DataArea
                && ledgerIntercompany.DestinationCompany == curext()
        join RecId from projParameters
            where projParameters.dataAreaId == companyTable.DataArea
            && projParameters.EnableIntercompanyTimesheet == NoYes::Yes
        join RecId from employmentCalendar
            where employmentCalendar.Employment == employmentTable.RecId
            && employmentCalendar.CalendarDataAreaId == companyTable.DataArea
            && employmentCalendar.CalendarId != ""
        join RecId from projectTransferPrice
            where projectTransferPrice.dataAreaId == companyTable.DataArea
                && projectTransferPrice.ProjectDataAreaId == curext()
    && (ResourceFacade::getWorker(projectTransferPrice.Resource) == _hcmWorkerRecId ||
                    projectTransferPrice.Resource == 0);

    return (employmentTable.RecId != 0);
}

private static void setVendorDataAreaId(FormControl _employmentContractorControl)
{
    // Retrieve the employment from the calling form
    FormRun hcmWorkerForm = _employmentContractorControl.formRun();
    FormDataSource employmentContractor_ds = hcmWorkerForm.dataSource(formDataSourceStr(HcmWorker, HcmEmploymentContractor)) as FormDataSource;
    HcmEmploymentContractor employmentContractor = employmentContractor_ds.cursor();

    if (employmentContractor.VendorDataAreaID == '')
    {
        // Retrieve the employment from the calling form
        FormDataSource hcmEmployment_ds = hcmWorkerForm.dataSource(formDataSourceStr(HcmWorker, HcmEmployment)) as FormDataSource;
        HcmEmployment employment = hcmEmployment_ds.cursor();

        //
        // The HcmEmploymentContractor.VendorDataAreaID field provides the DataArea containment
        // for both the Vendor and Purchase order selections.
        // Once set for either a Vendor or Purchase order selection it should not be changeable because it
        // must remain the same. Any subsequent Vendor or Purchase Order selection
        // must be valid for the existing DataArea containment.
        //
        employmentContractor.VendorDataAreaID = CompanyInfo::getDataArea(employment.LegalEntity);
    }
}

[FormDataSourceEventHandler(formDataSourceStr(HcmWorker, HcmEmploymentContractor), FormDataSourceEventType::Writing)]
public static void HcmEmploymentContractor_OnWriting(FormDataSource _sender, FormDataSourceEventArgs _e)
{
    HcmEmploymentContractor contractor = _sender.cursor();

    HcmEmploymentContractor existingContractor;

    select firstonly existingContractor
         where existingContractor.Employment == contractor.Employment;

    if (!existingContractor)
    {
        //If we are creating the initial record in the ContractorDetails, it should match up
        //with the from date on the employment.
        HcmEmployment employment = HcmEmployment::find(contractor.Employment);
        if (employment)
        {
            contractor.ValidFrom = employment.ValidFrom;
        }
    }
}

[FormDataSourceEventHandler(formDataSourceStr(HcmWorker, HcmEmployment), FormDataSourceEventType::SelectionChanged)]
public static void HcmEmployment_OnSelectionChanged(FormDataSource _sender, FormDataSourceEventArgs _e)
{
    FormRun hcmWorkerForm = _sender.formRun();

    FormDataSource fdsEmploymentContractor =  hcmWorkerForm.dataSource(formDataSourceStr(HcmWorker, HcmEmploymentContractor)) as FormDataSource;
    fdsEmploymentContractor.executeQuery();
}

/// <summary>
/// CFMDirPartyTable form data source OnInitValue event handler for HcmWorker form extension data source.
/// </summary>
/// <param name="_sender">The CFMDirPartyTable data source of the form extension.</param>
/// <param name="_e">Args for the event.</param>
[FormDataSourceEventHandler(formDataSourceStr(HcmWorker, CFMDirPartyTable), FormDataSourceEventType::InitValue)]
public static void CFMDirPartyTable_OnInitValue(FormDataSource _sender, FormDataSourceEventArgs _e)
{
    if (CFMParameters::isModuleEnabled())
    {
        CFMDirPartyTable cfmDirPartyTable = _sender.cursor();
        if (!cfmDirPartyTable.DirPartyTable)
        {
            DirPerson dirPerson = _sender.formRun().dataSource(formDataSourceStr(HcmWorker, DirPerson)).cursor();
            cfmDirPartyTable.DirPartyTable = dirPerson.RecId;
        }
    }
}

/// <summary>
/// Event handler for the Initialized event of the DirPartyTable form.
/// </summary>
/// <param name="sender">The <c>CFMDirPartyTable</c> data source.</param>
/// <param name="e">The args for the event.</param>
[FormDataSourceEventHandler(formDataSourceStr(HcmWorker, CFMDirPartyTable), FormDataSourceEventType::Initialized)]
public static void CFMDirPartyTable_OnInitialized(FormDataSource sender, FormDataSourceEventArgs e)
{
    if (!CFMParameters::isModuleEnabled() && sender)
    {
        QueryBuildDataSource dirPartyQbds = sender.queryBuildDataSource();
        dirPartyQbds.enabled(false);
        sender.linkType(FormLinkType::Passive);
    }
}
