/// <summary>
///    Builds the list of related image resources that are related to the kanban feature.
/// </summary>
protected void build()
{
    super();
    this.add(#ImageKanbanJobCreated);
    this.add(#ImageKanbanJobPlanned);
    this.add(#ImageKanbanJobPrepared);
    this.add(#ImageKanbanJobInProgress);
    this.add(#ImageKanbanHandlingUnitReceived);
    this.add(#ImageKanbanJobEnded);
    this.add(#ImageKanbanJobOverdue);
    this.add(#ImageKanbanJobPriority);
}

/// <summary>
///    Initializes a new <c>ImageListAppl_Kanban</c> object.
/// </summary>
/// <returns>
///    A new <c>ImageListAppl_Kanban</c> object.
/// </returns>
public static ImageListAppl_Kanban construct()
{
    return new ImageListAppl_Kanban();
}
