Method.Source:
/// <summary>
///     Logs an error message.
/// </summary>
/// <param name="_message">
///     The error message.
/// </param>
/// <param name="_errorCode">
///     The error code.
/// </param>
public void logError(str _message, str _errorCode = '')
{
    checkFailed(_message);
}

/// <summary>
///     Logs an information message.
/// </summary>
/// <param name="_message">
///     The information message.
/// </param>
public void logInfo(str _message)
{
    info(_message);
}

/// <summary>
///     Logs a warning message.
/// </summary>
/// <param name="_message">
///     The warning message.
/// </param>
public void logWarning(str _message)
{
    warning(_message);
}
