SourceCode.Declaration:
using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>IntercompanyAutoCreatePurchaseOrderFeature</c> class defines the feature management feature,
/// when enabled provides automatic creation of intercompany orders after creation of purchase orders.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public final class IntercompanyAutoCreatePurchaseOrderFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static IntercompanyAutoCreatePurchaseOrderFeature singleton;

}

Method.Source:
[Hookable(false)]
public static IntercompanyAutoCreatePurchaseOrderFeature instance()
{
    if (!singleton)
    {
        singleton = new IntercompanyAutoCreatePurchaseOrderFeature();
    }

    return singleton;
}
