using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.Implementation;

/// <summary>
/// When <c>LedgerConsolidateTemplateFeature</c> feature is enabled,
/// users will be able to create and use LedgerConsolidateTemplate to avoid manual entries for each consolidation run.
/// All consolidate parameters will be saved as LedgerConsolidateTemplate.
/// </summary>
[Export(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class LedgerConsolidateTemplateFeature implements IFeatureMetadata
{
    private static LedgerConsolidateTemplateFeature instance = new LedgerConsolidateTemplateFeature();

}

Method.Source:
[Hookable(false)]
public static LedgerConsolidateTemplateFeature instance()
{
    return instance;
}

private static void TypeNew()
{
    instance = new LedgerConsolidateTemplateFeature();
}
