/// <summary>
/// Gets an array of instances of all <c>LedgerFiscalPeriodRecalculation</c> classes.
/// </summary>
/// <returns>
/// An <c>Array</c> of instances of classes extending the <c>LedgerFiscalPeriodRecalculation</c> class.
/// </returns>
internal static Array getLedgerFiscalPeriodRecalculation()
{
    return SysPluginFactory::Instances(
        identifierStr(Dynamics.AX.Application),
        classStr(LedgerFiscalPeriodRecalculation),
        new SysPluginMetadataCollection());
}
