using FeatureLifecycleStage = Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.FeatureLifecycleStage;

/// <summary>
/// The <c>LedgerJournalForcedJournalSplitFeature</c> class forces journal splitting for journals above the limit defined in LedgerJournalMaxLineLimit
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
internal final class LedgerJournalForcedJournalSplitFeature implements IFeatureLifecycle, IFeatureMetadata
{
    private static LedgerJournalForcedJournalSplitFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new LedgerJournalForcedJournalSplitFeature();
}

/// <summary>
/// Determines whether the feature is enabled.
/// </summary>
/// <returns>
/// Returns true if the feature in Feature Management is enabled; otherwise, false.
/// </returns>
[Hookable(false)]
public static boolean isEnabled()
{
    return Dynamics.AX.Application.FeatureStateProvider::isFeatureEnabled(LedgerJournalForcedJournalSplitFeature::instance());
}

[Hookable(false)]
public FeatureLifecycleStage FeatureStage()
{
    // The flight is listed as Released but it is based on the guidance for IFeatureLifecycle which allows this flight to still act as a "kill switch".
    return FeatureLifecycleStage::Released;
}
