/// <summary>
///    Sets or gets the currency code.
/// </summary>
/// <param name="_currencyCode">
///    The currency code.
/// </param>
/// <returns>
///    The currency code.
/// </returns>
public CurrencyCode parmCurrencyCode(CurrencyCode _currencyCode = currencyCode)
{
    currencyCode = _currencyCode;
    return currencyCode;
}

